import React,{useState} from 'react'
import { apiKey } from '../constanst';

const LocationSearch = ({ onCityFound }) => {
    const [cityName, setCityName] = useState('');


    const fetchWeatherData = (cityName) => {
        fetch(`http://dataservice.accuweather.com/locations/v1/cities/search?apikey=${apiKey}&q=${cityName}&language=es`)
            .then(res => res.json())
            .then(res => res.find(l => l.Country.ID === 'US'))
            .then(res => onCityFound({
                name: res.LocalizedName,
                key: res.Key,
                state: res.AdministrativeArea.ID
            })) 
    }
    
    return (
        <div>
            <input type="text" onKeyDown = {e => {if (e.key === 'Enter'){
                fetchWeatherData(cityName)
            }}} onChange={e => {
                setCityName(e.target.value);
            }}/>
        </div>
    )
}

export default LocationSearch
