import React, {
  useState,useEffect
} from 'react';
import {apiKey} from './constanst'
import './App.css'
import Weather from './Components/Weather';
import LocationSearch from './Components/LocationSearch';

const App = () => {

const [locationKey, setLocationKey] = useState('');

const [weatherInfo, setWeatherInfo] = useState();

const padNum = (num) => {
  const stringNum = num + '';
  const stringLen = stringNum.length;

  if (stringLen === 1){
    return '0' + stringNum; // 4-> 04
  }else{
    return stringNum; // 17 -> 17
  }
};


useEffect(() => {
    if (locationKey){
      fetch(`http://dataservice.accuweather.com/forecasts/v1/daily/5day/${locationKey}?apikey=${apiKey}&language=es&metric=true`)
        .then(res => res.json())
        .then(res => {
          console.log(res);
          setWeatherInfo(res.DailyForecasts
          .map(df => {
            return {
              min: df.Temperature.Minimum.Value,
              max: df.Temperature.Maximum.Value,
              weatherKey: padNum(df.Day.Icon),
              weatherType: df.Day.IconPhrase,
            }
      }))
    });
  }
}, [locationKey])



  return ( 
    <div>

      <LocationSearch
      onCityFound = {cityInfo =>{
        setLocationKey(cityInfo.Key);
      }}
      />

      <div className='main'>
          {!!weatherInfo && weatherInfo.map((i,index) => (
          <div className='day' key={index}>
            <Weather 
            min={i.min}
            max={i.max} 

            weatherKey={i.weatherKey}/>
          </div>))}
      </div>
    </div>
  );
}


export default App
