import React, {
  useEffect,
  useState
} from 'react';

import Weather from './components/Weather';

const Initialurl = `https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=${apiKey}`
const cityName = 'corrientes';
const apiKey = '8a5e9515a6583a0a93a8e614d848ffb5';


const App = () => {

  const [weatherInfo, setWeatherInfo] = useState([]);

  const fetchWeatherData = (cityName) => {
    fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=${apiKey}`)
      .then(response => response.json())
      .then(data => setWeatherInfo(data.list))
      .catch(error => console.log(error))
  }

  useEffect(() => {
    fetchWeatherData(Initialurl);
  }, )


  return ( 
    <div className = 'container'>

      <Weather weatherInfo={weatherInfo}/>
    </div>
  );
}


export default App
