import React from 'react'
import Spinner from './Spinner'

const Card = ({loadingData, weather, forecast, showData}) => {

    var today = new Date();
    var day = today.getDate();
    var month = today.getMonth() + 1;
    var year = today.getFullYear();
    var date = day + '/' +  month + '/' + year;

    var url ='';
    var iconUrl = '';

    if(loadingData){
        return <Spinner/>
    }

    if(showData){
        url='http://openweathermap.org/img/w/';
        iconUrl = url + weather.weather[0].icon + '.png';
    }

    return (
        <div className='mt-5'>

            {
                showData === true ? (
                    <div className='container'>
                        <div className="card mb-3 mx-auto bg-dark text-light">
                            <div className="row g-0">
                                <div className="col-md-4">
                                    <h3 className='card-title'>{weather.name}</h3>
                                    <p className='card-date'>{date}</p>
                                    <h1 className='card-temp'>{(weather.main.temp)}Â°C</h1>
                                    <p className='card-desc'><img src={iconUrl} alt="icon" />{weather.weather[0].description}</p>
                                    <img src="https://images.pexels.com/photos/2114970/pexels-photo-2114970.jpeg" className='img-fluid rounded-start' alt="cityImage" />
                                </div>

                                <div className="col-md-8">
                                    <div className="card-body text-start mt-2">

                                    </div>
                                </div>

                            </div>
                        </div>

                    </div>
                ):(
                    <h2 className='text-light'>Sin Datos</h2>
                )
            }

        </div>
    )
}

export default Card
