}, [weatherInfo])

  return ( 
    <div>
      {!!weatherInfo && weatherInfo.map((i,index) => (
      <div key={index}>
        <Weather min={i.min}
        max={i.max} 
        weatherType={i.weatherType} 
        weatherKey={i.weatherKey}/>
      </div>))}
    </div>
  );
}


export default App
