import { useState, useEffect } from "react";

const CityButton = ({ cityDefault }) => {
    
    const [city, setCity] = useState('')

    const handleButton = (e) =>{
        e.preventDefault();
        setCity(e.target.value);
        console.log(city);
        cityDefault(city)
    }

    useEffect(() => {
        return () => {
            setCity(city)
        };
    },[city])

    return (
        <div className="cities-container">
                <div>
                    <button onClick={handleButton} value='London' className='city-button'>London</button>
                </div>
                <div>
                    <button onClick={handleButton} value='Tokyo' className='city-button'>Tokyo</button>
                </div>
                <div>
                    <button onClick={handleButton} value='Barcelona' className='city-button'>Barcelona</button>
                </div>
                <div>
                    <button onClick={handleButton} value='Madrid' className='city-button'>Madrid</button>
                </div>
                <div>
                    <button onClick={handleButton} value='Caracas' className='city-button'>Caracas</button>
            </div>
        </div>
    )
}

export default CityButton
